<?phpnamespace App;use Illuminate\Database\Eloquent\Model;class UserPackage extends Model{      public const  Active = 1;    public  const  disabled = 0;        protected $fillable = [        'user_id',        'package_id',        'starting_paid_date',        'ending_paid_date',        'post_per_package',        'free_post_per_package',        'paid_status',          'auction_adds',        'wanted_adds',        'swap_adds',        'pres_adds',        'misc_adds',        'garage_adds',        'featured',        'amr_add',        'body_shop_adds',        'rental_companie_adds',        'class_add',        'status',        "no_of_times"    ];    protected $table = 'users_packages';    public function package()    {        return $this->belongsTo('App\Package', 'package_id', 'id');    }    public function user()    {        return $this->belongsTo(User::class, "user_id");    }    public function u_package()    {        return $this->belongsTo('App\Package', 'package_id', 'id');    }    public function userPackagePostLimit()    {        return $this->hasOne('App\UserPackagePostLimit', 'user_package_id', 'id');    }}