<?phpnamespace App;use Illuminate\Database\Eloquent\Model;class UserPackagePostLimit extends Model{    protected $fillable = [        'user_package_id',        'package_id',        "post_limit",        "user_id",    ];    protected $table = 'user_package_post_limits';    public function package()    {        return $this->belongsTo('App\Package', 'package_id', 'id');    }    public function user()    {        return $this->belongsTo(User::class, "user_id");    }    public function user_package()    {        return $this->belongsTo('App\UserPackage', 'user_package_id', 'id');    }}