<script src="https://apis.google.com/js/api:client.js"></script><script>    function social_login(user_details){        let {id,username,email,profile_image}=user_details;     let formdata=new FormData();     var details=username.split(' ');     formdata.append("social_id",id);     formdata.append("username",username);     formdata.append("email",email);     formdata.append("first_name",details[0]);     formdata.append("last_name",details[1])        formdata.append("package_id",$("#p_id_user_login").val());     formdata.append("profile_pic",profile_image);        $.ajaxSetup({            headers: {            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')            }        });        $.ajax({            method:"post",            url:"{{route('social-login-submit')}}",            data:formdata,            datatype:"json",            cache: false,            processData: false,            contentType: false,            success:function (data){            if(data.g_route === "1"){                window.location= "{{route("garage")}}";                }else if(data.w_route === "swap"){                var id =  btoa(data.s_id)  ;                var r_url  = "{{route('frontend-swap-cars',['s_id'=>':id'])}}";                r_url =    r_url.replace(":id",id);                window.location = r_url;            }else if(data.blog_route === "blog"){                window.location= "{{route("frontend-blog")}}";                }else {                window.location=data.result;            }        },            error:function (data){        }        });    }    var googleUser = {};    var startApp = function() {    gapi.load('auth2', function(){        auth2 = gapi.auth2.init({                client_id: '144900792445-n28jg812c4mia446odtamba04r43ej7p.apps.googleusercontent.com',                cookiepolicy: 'single_host_origin',            });            attachSignin(document.getElementById('googlebtn'));        });};    function attachSignin(element) {        auth2.attachClickHandler(element, {},            function(googleUser) {                var profile=googleUser.getBasicProfile();                let user_details={                    id:profile.getId(),                     username:  profile.getName(),                     email:   profile.getEmail(),                     profile_image:profile.getImageUrl(),                };                social_login(user_details);            }, function(error) {        });    }    startApp();</script><script>window.fbAsyncInit = function() {    FB.init({            appId      : '153999753160990',            xfbml      : true,            version    : 'v10.0'        });        FB.getLoginStatus((response) => {                     if (response.status === 'connected') {             FB.logout(function(response) { });                        // FB.api('/me', {fields: 'id,first_name,last_name,email,picture'}, function(response) {            //     console.log(response);            //            // });            FB.api('/me/picture?width=180&height=180', function (response) {                console.log(response);            });        } else {        }    });    };     document.getElementById('facbookbtn').addEventListener('click', () => {    FB.login((response) => {        if (response.authResponse) {             document.getElementById('facbookbtn').style.display = 'none';            FB.api('/me', {fields: 'first_name,last_name,email,picture'}, function(profile) {                let user_details={                    id:profile.Id,                        username:  profile.first_name+" "+profile.last_name ,                        email:   profile.email,                        profile_image:profile.picture.data.url,                    };                    social_login(user_details);                });                FB.api('/me/picture?width=180&height=180', function (response) {                    console.log(response);                });                           }    });    }, false);</script><script async defer crossorigin="anonymous" src="https://connect.facebook.net/en_US/sdk.js"></script>